INSERT INTO `wp_postmeta` VALUES (11813,732,'description','<span>(Subject meets with </span><a href=\"http://student.mit.edu/catalog/mSTSa.html#STS.026\">STS.026</a><span>)</span>\r\n<span>Prereq: None</span>\r\n<span>Units: 3-0-9</span>\r\n<img src=\"http://student.mit.edu/icns/hr.gif\" alt=\"______\" /><span></span>\r\n<span>Introductory survey of fundamental innovations and transitions in American manufacturing from the colonial period to the mid-twentieth century. Primary emphasis on textiles and metalworking, with particular attention to the role of the machine tool industry in the American manufacturing economy. Students taking graduate version are expected to explore the material in greater depth.</span>\r\n<i>M. R. Smith</i>');
