INSERT INTO `wp_postmeta` VALUES (12891,804,'first_name','Julia');
INSERT INTO `wp_postmeta` VALUES (12892,804,'last_name','Menzel');
INSERT INTO `wp_postmeta` VALUES (12893,804,'bio','Julia Menzel is a PhD candidate in MIT\'s program in History; Anthropology; and Science, Technology, and Society (HASTS). Her research focuses on the history of modern physics and related mathematical and computational sciences from the 1970s to the present. She is currently writing a dissertation about a crisis of natural order in late-twentieth-century theoretical physics.\r\n\r\nJulia was trained in Physics at Yale University (BS, 2017) and MIT (SM, in progress) and in History and Philosophy of Science at the University of Cambridge (MPhil, 2018), where she was a Gates-Cambridge Scholar. Her dissertation research is supported by the National Science Foundation and several other academic institutions. In 2022-2023, she is a long-term predoctoral fellow at the American Philosophical Society in Philadelphia. Her work has been published in Historical Studies in the Natural Sciences.');
INSERT INTO `wp_postmeta` VALUES (12894,804,'email','julimen@MIT.EDU');
INSERT INTO `wp_postmeta` VALUES (12895,804,'current_research_areas','history of science, science and technology studies, physics, mathematical sciences, computing and computational sciences, political economy, labor, critical theory, historical epistemology');
