INSERT INTO `wp_postmeta` VALUES (12897,805,'first_name','George');
INSERT INTO `wp_postmeta` VALUES (12898,805,'last_name','Paiva Sieniawski');
INSERT INTO `wp_postmeta` VALUES (12899,805,'bio','Before his move to Cambridge, MA, George spent over a decade leading interdisciplinary research efforts for institutional investors and U.S. government mission owners. Now an MIT Presidential Fellow based in the Institute\'s Ph.D. Program in History, Anthropology, and Science, Technology, and Society, he holds an M.Sc. in Data Visualization from Parsons/The New School\'s Art, Media, and Technology faculty and a B.Sc. in Foreign Service from Georgetown University\'s School of Foreign Service in Washington, DC.');
