INSERT INTO `wp_postmeta` VALUES (12970,817,'last_name','Wu');
INSERT INTO `wp_postmeta` VALUES (12971,817,'bio','Di’s research and practice focus on the relationship between disability, labor, and technology in contemporary China. Her dissertation project examines the labor, expertise, and activism of disabled persons in China\'s tech industry, connecting the current moment with the history of disability innovation, disability during and after state socialism, and China’s disability movement in general. Di’s research is informed by her five-year experience in managing United Nations agency programs on disability justice and co-founding a disability organization in China. Her work includes promoting inclusive employment, and facilitating China’s implementation of the Convention on the Rights of Persons with Disabilities (CRPD).');
INSERT INTO `wp_postmeta` VALUES (12972,817,'email','wdi@mit.edu');
INSERT INTO `wp_postmeta` VALUES (12973,817,'current_research_areas','disability studies; labor/work; history and culture of computing/AI; biopolitics; rehabilitation; state socialism; activism; China studies; medical anthropology; science and technology studies');
